package main

import (
	"encoding/hex"
	"fmt"
	"github.com/ethereum/go-ethereum/core/types"
)

func main() {

	txDataHex := ""
	fmt.Printf("tx data: %s\n", txDataHex)

	txInAgg := &types.Transaction{}
	rawTxBytes, err := hex.DecodeString(txDataHex[2:])
	if err != nil {
		fmt.Println("err:", err)
	}

	err = txInAgg.UnmarshalBinary(rawTxBytes)
	if err != nil {
		fmt.Println("err:", err)
	}

	//spew.Dump(txInAgg)

	// 打印解析结果
	fmt.Printf("Tx Type: %x\n", txInAgg.Type())

	fmt.Printf("Nonce: %v\n", txInAgg.Nonce())
	fmt.Printf("GasPrice: %v\n", txInAgg.GasPrice())
	fmt.Printf("GasLimit: %v\n", txInAgg.Gas())
	fmt.Printf("To: %v\n", txInAgg.To())
	fmt.Printf("Value: %v\n", txInAgg.Value())
	fmt.Printf("Data: %x\n", txInAgg.Data())

	v, r, s := txInAgg.RawSignatureValues()
	fmt.Printf("V: %v\n", v.String())
	fmt.Printf("R: %v\n", r.String())
	fmt.Printf("S: %v\n", s.String())

}
